package week1.assignments;

public class VariablesAndDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String brandName= "Oneplus";
		char color='G';
		int price= 50000;
		byte memory =126 ;
		float screenSize=5.6f;
		boolean isTouchScreen= true;
		long phoneNo= 1234567891L;
		
		
		System.out.println("Price of " + brandName + " is " +price);
		System.out.println("Colour of " + brandName + " is " + color);
		System.out.println("Brand name: " + brandName );
		System.out.println("Memory of " + brandName + " is " + memory + " bytes");
		System.out.println("Screen size:---> " +screenSize );
		System.out.println("Touch screen specification: " +isTouchScreen);
		System.out.println("Phone number of the " + brandName + " mobile is " +phoneNo);
		
		
	}

}
